const initialState = {
  good: 0,
  ok: 0,
  bad: 0
}

const counterReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'GOOD':
      const oldValue = state.good
      const newState = {...state, good: +1}
      return newState
    case 'NEUTRAL':
      return state + 1
    case 'BAD': 
      return state + 1
    case 'RESET':
      return state
    default: return state
  }

}

export default counterReducer
