const initialState = {
  good: 0,
  ok: 0,
  bad: 0
}

const counterReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'GOOD':
      const updatedGood = {...state, good: state.good + 1}
      return updatedGood
    case 'NEUTRAL':
      const updatedNeutral = {...state, neutral: state.neutral + 1}
      return updatedNeutral
    case 'BAD': 
      return state + 1
    case 'RESET':
      return state
    default: return state
  }

}

export default counterReducer
